import {
  Scene,
  Color,
  Mesh,
  MeshNormalMaterial,
  BoxBufferGeometry,
  PerspectiveCamera,
  WebGLRenderer,
  OrthographicCamera,
  Clock,
  AudioListener,
} from "three";
import CubeScreen from "./screen";
import { OrbitControls } from "three/examples/jsm/controls/OrbitControls";
import DigitalNumber from "./number";
import Tetris from './gameLogic';
import Score from "./score";
class Main {
  /** The scene */
  public scene: Scene;

  /** The camera */
  public camera: PerspectiveCamera | OrthographicCamera;

  /** The renderer */
  public renderer: WebGLRenderer;

  /** The orbit controls */
  public controls: OrbitControls;

  /** The cube mesh */
  public cube: Mesh;

  public screen: CubeScreen;

  public tetris: Tetris;

  pause:boolean;

  clock: Clock;
  gameTime:number;
  constructor() {
    this.initViewport();
  }

  /** Initialize the viewport */
  public initViewport() {
    this.pause = false;
    this.clock = new Clock();
    // Init scene.
    this.scene = new Scene();
    this.gameTime = 0;
    this.scene.background = new Color("#566356");

    // Init camera.
    const aspect = window.innerWidth / window.innerHeight;
    // this.camera = new PerspectiveCamera(60, aspect, 0.1, 100);

    const basicHeight = 32;
    const basicWidth = basicHeight * aspect;
    this.camera = new OrthographicCamera(-basicWidth/2,basicWidth/2,basicHeight/2,-basicHeight/2,0.1,20);
    this.camera.position.z = 10;

    const listener = new AudioListener();
    this.camera.add(listener);

    // Init renderer.
    this.renderer = new WebGLRenderer({
      powerPreference: "high-performance",
      antialias: true,
    });
    this.renderer.setPixelRatio(window.devicePixelRatio);
    this.renderer.setSize(window.innerWidth, window.innerHeight);
    this.renderer.render(this.scene, this.camera);

    document.body.appendChild(this.renderer.domElement);
    window.addEventListener("resize", () => this.onResize());


    // Init orbit controls.
    this.controls = new OrbitControls(this.camera, this.renderer.domElement);
    this.controls.update();
    this.controls.enabled = false;
    // this.controls.addEventListener("change", () => this.render());


    // Add test mesh.
    this.cube = this.createCubeMesh();
    const screenWidth = 12;
    const screenHeight = 18;
    const screen = new CubeScreen(screenWidth,screenHeight);
    screen.renderObj.position.set(-screenWidth/2+1,basicHeight/2-4,0);
    this.screen = screen;
    this.scene.add(screen.renderObj);

    const num = new DigitalNumber();
    num.group.position.set(2,-9,0);
    this.scene.add(num.group);

    const score = new Score(5);
    this.scene.add(score.renderObj);
    num.updateNumber(1);
    this.tetris = new Tetris(screenWidth,screenHeight,listener);
    this.renderer.setAnimationLoop(() => this.animate()); // uncomment if you want to use the animation loop
    window.addEventListener("blur",()=>{
      this.pause = true;
      this.clock.stop();
    })

    window.addEventListener("focus",()=>{
      this.pause = false;
      this.clock.start();
    });
    this.addEvent();
  }

  setAudio(){
    const listener = new AudioListener();
    this.camera.add(listener);

    return listener;
  }

  addEvent(){
    window.addEventListener("keydown",(e)=>{
      const key = e.key.toUpperCase();
      if( e.code === "Space"){
        this.tetris.actionRotate();
        return;
      }
      switch(key){
        case 'A':
          this.tetris.actionMoveLeft();
          break;
        case 'S':
          this.tetris.actionMoveDown();
          break;
        case 'D':
          this.tetris.actionMoveRight();
          break;
      }
    })
    const startButton = document.getElementById("startButton");
    if( startButton ) {
      startButton.addEventListener("click",()=>{
        console.log("start");
        if( this.tetris.gameState === "process" ) {

          // this.clock.start();
          // this.tetris.reStart();
        } else {
          this.tetris.reStart();
        }


        // startButton.hidden = true;
      })
    }
  }

  /** Renders the scene */
  public render() {
    this.renderer.render(this.scene, this.camera);
  }

  /** Animates the scene */
  public animate() {
    if(this.pause) return;
    this.controls.update();
    this.logicUpdate();
    this.screen.update();
    this.renderer.render(this.scene, this.camera);

  }

  logicUpdate(){
    const delta = this.clock.getDelta();
    this.gameTime += delta;

    const currentTime = this.clock.getElapsedTime();
    this.screen.setAlpha(this.tetris.outputBlockBuffer)
    this.tetris.update(delta, this.gameTime);
  }

  makeExampleScreen(){
    const screenArray = new Array(8*16).fill(0);
    screenArray[3] = 1;
    this.screen.setAlpha(screenArray);
  }

  /** On resize event */
  public onResize() {
    if (this.camera instanceof PerspectiveCamera) {
      this.camera.aspect = window.innerWidth / window.innerHeight;
      this.camera.updateProjectionMatrix();
    }
    if( this.camera instanceof OrthographicCamera ) {
      const aspect = window.innerWidth / window.innerHeight;
    // this.camera = new PerspectiveCamera(60, aspect, 0.1, 100);

      const basicHeight = 32;
      const basicWidth = basicHeight * aspect;
      this.camera.left = -basicWidth/2;
      this.camera.right = basicWidth/2;
      this.camera.top = basicHeight/2;
      this.camera.bottom = -basicHeight/2;
      this.camera.updateProjectionMatrix();
    }
    this.renderer.setSize(window.innerWidth, window.innerHeight);
    this.render();
  }

  /** Creates a cube mesh */
  public createCubeMesh() {
    const geometry = new BoxBufferGeometry(1, 1, 1);
    const material = new MeshNormalMaterial();
    const mesh = new Mesh(geometry, material);
    return mesh;
  }


}

new Main();
